import React, {
    Component
} from 'react'
import ReactDOM from 'react-dom'

class Main extends Component {
    constructor(props) {
        super(props)

        this.state = {
            minutes: '5',
            seconds: '00',
            intervalID: null
        }

        this.handleChange = this.handleChange.bind(this)
        this.secondsRemaining
        this.handleTimeInterval

        // Trigger Countdown functionality
        this.handleCountdown = this.handleCountdown.bind(this)
        this.tick = this.tick.bind(this)

        // Pause the Clock
        this.pauseTheClock = this.pauseTheClock.bind(this)
    }

    handleChange(event) {
        const {
            name,
            value
        } = event.target
        this.setState({
            minutes: event.target.value
        })
    }

    tick() {
        var min = Math.floor(this.secondsRemaining / 60)
        var sec = this.secondsRemaining - (min * 60)

        this.setState({
            minutes: min,
            seconds: sec
        })

        if (sec < 10) {
            this.setState({
                seconds: "0" + this.state.seconds
            })
        }

        if (min < 10) {
            this.setState({
                value: "0" + min
            })
        }

        if (min === 0 && sec === 0) {
            clearInterval(this.handleTimeInterval)
        }

        this.secondsRemaining--

    }

    pauseTheClock() {
        clearInterval(this.state.intervalID)
    }

    handleCountdown() {
        const startText = "Start"
        const pauseText = "Paused"
        this.handleTimeInterval = setInterval(this.tick, 1000)
        let time = this.state.minutes

        this.secondsRemaining = time * 60


        if (this.state.pauseTheClock) {
            this.setState({
                intervalID: window.setInterval(1000)
            })
        }
    }



    render() {
        return ( <
            div >
            <
            Header / >
            <
            TimerInput minutes = {
                this.state.minutes
            }
            handleChange = {
                this.handleChange
            }
            /> <
            Timer minutes = {
                this.state.minutes
            }
            seconds = {
                this.state.seconds
            }
            /> <
            StartButton handleCountdown = {
                this.handleCountdown
            }
            /> <
            /div>
        )
    }
}

function Header() {
    return ( <
        h1 > Pomodoro Counter < /h1>
    )
}

class TimerInput extends Component {
    render() {
        return ( <
            div className = "timeInput" >
            <
            input type = "number"
            minutes = {
                this.props.minutes
            }
            placeholder = "Your time here"
            required onChange = {
                this.props.handleChange
            }
            /> <
            /div>
        )
    }
}

class Timer extends Component {
    render() {
        return ( <
            div className = "timerDisplay" >
            <
            h1 > {
                this.props.minutes
            }: {
                this.props.seconds
            } <
            /h1> <
            /div>
        )
    }
}

class StartButton extends Component {
    render() {
        return ( <
            div >
            <
            button onClick = {
                this.props.handleCountdown
            } > Start < /button> <
            /div>
        )
    }
}

export default Main
